// var contentType = require('content-type');
var contentType = require('../');

class Runner {
  constructor() {}
  parse() {
    var obj = contentType.parse('image/svg+xml; charset=utf-8');
    console.log(obj);
  }
  format() {
    var str = contentType.format({
      type: 'image/svg+xml',
      parameters: { charset: 'utf-8' }
    });
    console.log(str);
  }
}

const obj = new Runner();
console.log('parse');
obj.parse();
console.log('format');
obj.format();