const Negotiator = require('../');

const request = {
  headers: {
    accept: 'text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3',
    'accept-encoding': 'gzip, deflate, br',
    'accept-language': 'zh-CN,zh;q=0.9,en;q=0.8,und;q=0.7'
  }
}

class Runner {
  constructor() {
    this.negotiator = new Negotiator(request);
  }

  mediaTypes() {
    const results = this.negotiator.mediaTypes();
    console.log(results);
  }

  languages() {
    const results = this.negotiator.languages();
    console.log(results);
  }
}

const obj = new Runner();
// obj.mediaTypes();
obj.languages();