const pathToRegexp = require('path-to-regexp');

class Case {
  constructor() {}
  case1() {
    const str = '/profile/service/:id(\\d+)?';
    const reg = pathToRegexp(str);
    console.log(reg);
    const func = pathToRegexp.compile(str)
    console.log(func({id: 123}));
    console.log(reg.test('/profile/service/123'));
    console.log(reg.test('/profile/service/'));
    console.log(reg.test('/profile/service'));
  }
}

const obj = new Case();
obj.case1();
